                        placeholder="Введите ваш email..."
                    />
                    <Form.Control 
                        className="mt-3"
                        placeholder="Введите ваш пароль..."
                    />
                    <Button
                        className="mt-3 align-self-end"
                    >

                    </Button>
                </Form>
            </Card>
        </Container>
    );
};

export default Auth;
