import React, { useEffect, useState } from "react";
import { Col, Container } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import { useParams } from "react-router-dom";
import { fetchHouse, fetchOneHouse } from "../http/houseApi";

const HousePage = () =>{
    const house1 = {id:1, name: "room-1", address: 'Ул. Союзная, д. 139', description: 'Комфортабельный дом с двумя этажами', price: 20000, img: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Frealnoevremya.ru%2Farticles%2F143273-kottedzh-s-zemley-luchshaya-alternativa-kvartire&psig=AOvVaw2pjaFq5SedCK1g7rdA0XlW&ust=1653928373506000&source=images&cd=vfe&ved=0CAwQjRxqFwoTCNDgq76RhfgCFQAAAAAdAAAAABAD'};

    const[house, setHouse] = useState({info:[]});
    const {id} = useParams();
    useEffect(() => {
        fetchOneHouse(id).then(data => setHouse(data));
    }, [])
    return (
        <Container className = "mt-3">
            <Col md={6}>
            <Image width={300} height={300} src={house.img}/>
            <h2>{house.name}</h2>
            <h3>Price: {house.price}</h3>
            <p>Addres: {house.address}</p>
            <p>Description: {house.description}</p>
            </Col>

            <Col md={6}>
            </Col>
        </Container>
    );
};

export default HousePage;
